mapper.select:
select * from movie where 1 = 1

	
		
			
				and 1 = 1
			
			
				and title LIKE CONCAT('%', #{search}, '%')
			
		
	
	
		
			
				and mtype = #{mtype}
			
			
				and mtype = #{mtype} and title LIKE CONCAT('%',	#{search}, '%')
			
		
	

order by title asc limit #{start}, #{perPage}
