mapper.select:
select count(*) from movie where 1 = 1

	
		
			
				
					
						and 1 = 1
						
					
						and title like CONCAT('%',#{search},'%')
					
				
			
			
			
				
				
					and mtype = #{mtype}
				
				
					and mtype = #{mtype} and title like CONCAT('%',#{search},'%')
				
				
			
		
		
	
		and mpk = #{mpk}
