mapper.select:
select count(*) from room where 1 = 1

	
		
			
				
					
						and 1 = 1
						
					
						and title LIKE CONCAT(‘%’, #{search}, ‘%’)
					
				
			
			
			
				
				
					and mtype = #{mtype}
				
				
					and mtype = #{mtype} and title LIKE CONCAT(‘%’, #{search}, ‘%’)
				
				
			
		
	
	
	
		and mpk = #{mpk}
